export function TechStackSection() {
	const t = useTranslations('TechStack');
	const [selectedCategory, setSelectedCategory] = useState<string>('all');

	const categories = [
		{ value: 'all', label: t('all') },
		{ value: 'languages', label: t('languages') },
		{ value: 'frontend', label: 'Front End' },
		{ value: 'backend', label: 'Back End' },
		{ value: 'mobile', label: 'Mobile' },
		{ value: 'tools', label: t('tools') },
	];

	const filteredTechs =
		selectedCategory === 'all'
			? techStack
			: techStack.filter(tech => tech.category === selectedCategory);

	return (
		<section id="tech" className="py-20 px-4">
			<div className="max-w-3xl mx-auto space-y-6">
				<h2 className="text-3xl font-bold text-foreground text-center md:text-left">
					{t('title')}
				</h2>

				<div className="flex items-center justify-center md:justify-start gap-2 flex-wrap">
					{categories.map(c => (
						<Button
							key={c.value}
							variant={selectedCategory === c.value ? 'default' : 'outline'}
							onClick={() => setSelectedCategory(c.value)}
						>
							{c.label}
						</Button>
					))}
				</div>

				<div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-6 gap-4">
					{filteredTechs.map(tech => (
						<div
							key={tech.name}
							className="group relative overflow-hidden rounded-lg p-4 transition-all duration-300 hover:scale-105 hover:shadow-lg"
							style={{
								backgroundColor: `${tech.brandColor}20`,
								border: `1px solid ${tech.brandColor}40`,
							}}
						>
							<div className="flex flex-col items-center space-y-3 text-center">
								{tech.icon}

								<span className="text-sm font-medium text-foreground group-hover:text-foreground/80">
									{tech.name}
								</span>
							</div>

							<div
								className="absolute inset-0 opacity-0 group-hover:opacity-10 transition-opacity duration-300"
								style={{ backgroundColor: tech.brandColor }}
							/>
						</div>
					))}
				</div>
			</div>
		</section>
	);
}
